game:service("Visit") local error local id = 12 local RESPAWN_TIME = 5 local msg = Instance.new("Message",workspace) function OnError() msg.Text = "Failed to execute script: ".. error end if tonumber(RESPAWN_TIME) then msg.Text = "Waiting for scripts"else error = "RESPAWN_TIME is not a number"OnError() return end if tonumber(id) then msg.Text = "Waiting for scripts"else error = "ID is not a number"OnError() return end if game.Players.LocalPlayer then error = "Player already exists" OnError() wait(4) msg.Parent = nil return end local plr = game.Players:createLocalPlayer(id) wait(1) msg.Text = "Waiting for character" game:service("RunService"):run(); plr:SetSuperSafeChat(false); plr:LoadCharacter(id); msg.Parent = nil while true do wait() if plr.Character.Humanoid.Health == 0 then wait(RESPAWN_TIME) plr:LoadCharacter(id); end end
